SELECT * FROM (SELECT A.*, ROWNUM AS RNUM FROM (select class.code,video_code,class_video.section_code,unit,unit_content,section_index,video_name,class_name, substr(to_char(NUMTODSINTERVAL(video_length, 'second')),12,8) lengthStr,video_filename,nickname, to_char(enroll_date,'YYYY-MM-DD') enroll_date from class_video join section on class_video.section_code=section.section_code join class on class.code=class_video.code join member on member.userid=class.userid where to_char(enroll_date,'yyyy-mm-dd')>='${startDate}' and to_char(enroll_date,'yyyy-mm-dd')<='${endDate}'
and class.category='${category}'
and class.category='${category}' and (class_video.video_code like '%${searchWord}%' or class_video.video_name like '%${searchWord}%' or class.class_name like '%${searchWord}%' or class_video.video_filename like '%${searchWord}%' or member.nickname like '%${searchWord}%')
and (class_video.video_code like '%${searchWord}%' or class_video.video_name like '%${searchWord}%' or class.class_name like '%${searchWord}%' or class_video.video_filename like '%${searchWord}%' or member.nickname like '%${searchWord}%')
) A WHERE
ROWNUM <=${endNum} ) WHERE RNUM >=${startNum}

SELECT * FROM (SELECT A.*, ROWNUM AS RNUM FROM (select num , class_review.code , class.class_name, category,subject,class_review.grade ,member.username,to_char(writedate, 'yyyy-mm-dd') writedate from class_review join class on class.code = class_review.code join member on class_review.userid = member.userid where to_char(writedate, 'yyyy-mm-dd')>=#{startDate} and to_char(writedate, 'yyyy-mm-dd')<=#{endDate}
and category='${category}'
and class_review.grade='${grade}'
and (class.code like '%${search}%' or class_review.userid like '%${search}%' or member.username like '%${search}%'or class.class_name like '%${search}%')
and category='${category}' and class_review.grade='${grade}'
and class_review.grade='${grade}' and (class.code like '%${search}%' or class_review.userid like '%${search}%' or member.username like '%${search}%'or class.class_name like '%${search}%')
and category='${category}' and (class.code like '%${search}%' or class_review.userid like '%${search}%' or member.username like '%${search}%'or class.class_name like '%${search}%')
and category='${category}' and class_review.grade='${grade}' and (class.code like '%${search}%' or class_review.userid like '%${search}%' or member.username like '%${search}%'or class.class_name like '%${search}%')
) A WHERE ROWNUM <= ${endNum} ) WHERE RNUM >= ${startNum}

