SELECT * FROM
(SELECT A.*,
ROWNUM AS RNUM
FROM
(select code,category,class_name,nickname,total_student,to_char(signup,'YYYY-MM-DD') signup,   
to_char(allow,'YYYY-MM-DD') allow,class_state from class c join member m on c.userid=m.userid	



			where category='${category}' and to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			and to_char(signup, 'yyyy-mm-dd')<=#{endDate}


			where category='${category}' and to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}


			where category='${category}' and to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}
			and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


			where category='${category}' and to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}
			and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')
			and class_state='${class_state}' 



			where to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}


			and to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate} and class_state='${class_state}'


			 where to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}
			   and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


			where category='${category}'and class_state='${class_state}' 
			and to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			and to_char(signup, 'yyyy-mm-dd')<=#{endDate}
			and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


			where to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			and to_char(signup, 'yyyy-mm-dd')<=#{endDate}
			and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


			 where class_state='${class_state}'  
			 and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')
			 and to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			and to_char(signup, 'yyyy-mm-dd')<=#{endDate}


			 where category='${category}'
			and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')
			 and to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			and to_char(signup, 'yyyy-mm-dd')<=#{endDate}



			where category='${category}' and class_state='${class_state}' 
			and to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}



			where category='${category}' and class_state='${class_state}'  
			and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')



			  where to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}
			   and class_state='${class_state}'
			  and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


			  where to_char(${dateOption}, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(${dateOption}, 'yyyy-mm-dd')<=#{endDate}' and  category='${category}'
			  and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


			  where to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(signup, 'yyyy-mm-dd')<=#{endDate}
			  and  category='${category}'
			  and class_state='${class_state}'


			  where to_char(signup, 'yyyy-mm-dd')>=#{startDate}
			  and to_char(signup, 'yyyy-mm-dd')<=#{endDate}
			  and class_state='${class_state}'
			  and (code like '%${search}%' or class_name like '%${search}%' or nickname like '%${search}%')


order by code desc)A
WHERE
ROWNUM <= ${endNum}
)
WHERE
RNUM >=${startNum}
