mapper.select:
select code, category, goods_name, prime_cost, real_price , stock, sale_count, to_char(writedate , 'yyyy-mm-dd')writedate, 
sale_state, goods_info, goods_img1, goods_img2 from goods
where code = #{code}

SELECT * FROM (SELECT A.*, ROWNUM AS RNUM FROM (SELECT code, category, goods_name, prime_cost, real_price , stock,to_char(writedate, 'yyyy-mm-dd')writedate , sale_state from goods where to_char(writedate, 'yyyy-mm-dd')>= #{startDate} and to_char(writedate, 'yyyy-mm-dd')<= #{endDate}
and category='${category}'
and sale_State='${sale_State}'
and (goods_name like '%${search}%' or category like '%${search}%' or code like '%${search}%')
and sale_State='${sale_State}' and (goods_name like '%${search}%' or category like '%${search}%' or code like '%${search}%' )
and category='${category}' and sale_State='${sale_State}'
and category='${category}' and (goods_name like '%${search}%' or category like '%${search}%' or code like '%${search}%' )
and category='${category}' and sale_State='${sale_State}' and (goods_name like '%${search}%' or category like '%${search}%' or code like '%${search}%')
) A WHERE ROWNUM <= ${endNum} ) WHERE RNUM >= ${startNum}
