mapper.update:
update cart set amount = ${amount}
 where userid = '${userid}'
 and code='${code}'

mapper.insert:

declare
cnt number;
begin
    select count(userid) into cnt from good 
    where userid='${userid}' and code='${code}';
if cnt<1 then
insert into good(userid, code) values('${userid}', '${code}');
end if;
end;
